(()=>{class t{constructor(t){this.prices=document.querySelectorAll(".pricing-checkbox"),this.startPrice=1990,this.startMonthPrice=0,this.totalPrice=0,this.totalMonthPrice=0,this.assignEvents()}assignEvents(){this.prices.forEach(t=>{t.addEventListener("click",this.checkPrices.bind(this))})}checkPrices(){this.totalPrice=this.startPrice,this.totalMonthPrice=this.startMonthPrice,this.prices.forEach(t=>{let e=t.querySelector("input[type=checkbox]").checked;if(e){e.querySelector(".checkbox-title").innerText;let t=e.querySelector(".service-price"),e=t?+t.innerText.split("zł")[0]:0,i=e.querySelector(".tag.alternative"),c=i?+i.innerText.split("zł")[0]:0;this.totalPrice+=e,this.totalMonthPrice+=c}})}}document.addEventListener("DOMContentLoaded",function(e){new t})})();